var _ = require('lodash');
var Icon = require('./Icon.vue');
var CustomizationStore = require('../../stores/Customization');
var ChatViewStore = require('./ChatViewStore');

module.exports = {
  name: 'MessengerHeader',
  components: {
    'icon': Icon
  },
  props: {
    headerClientHeight: {
      type: Number,
      default: 0
    }
  },
  data: function() {
    return {
      ChatViewStoreState: ChatViewStore.state,
      notification_email: '',
      internal_notification_email: '',
      customs: CustomizationStore.state,
      isMember: false // computedでやりたいが..
    };
  },
  watch: {
    'ChatViewStoreState.user': function(val) {
      this.notification_email = _.get(this.ChatViewStoreState.user, 'values.identify.notification_email.any') || '';
      this.internal_notification_email = this.notification_email;
      this.isMember = this.ChatViewStoreState.user && this.ChatViewStoreState.user.user_id.indexOf('vis-') != 0;
    }
  },
  computed: {
    isModified: function() {
      return this.notification_email === '' || this.notification_email != this.internal_notification_email;
    }
  },
  methods: {
    closeMessenger: function(e) {
      e.preventDefault();
      e.stopPropagation();
      ChatViewStore.deactivate();
      this.$dispatch('deactivate'); // イベントの重複を防ぐためにChatPanel経由でdeactivate
    },
    registerNotificationEmail: function(e) {
      e.preventDefault();
      e.stopPropagation();
      this.internal_notification_email = this.notification_email;
      this.$dispatch('registerNotificationEmail', this.notification_email);
      this.closeSettingPanel();
    },
    unregisterNotificationEmail: function(e) {
      e.preventDefault();
      e.stopPropagation();
      this.notification_email = '';
      this.internal_notification_email = '';
      this.$dispatch('unregisterNotificationEmail');
    },
    toggleSettingDialog: function(e) {
      e.preventDefault();
      e.stopPropagation();
      
      if (this.ChatViewStoreState.isOpenSettingPanel) {
        ChatViewStore.closeSettingPanel();
      } else {
        ChatViewStore.openSettingPanel();
      }
    },
    closeSettingPanel: function() {
      ChatViewStore.closeSettingPanel();
    },
    stop: function(e) {
      e.stopPropagation();
    },
    cancelEnter: function(e) {
      if (e.keyCode == 13) {
        e.preventDefault();
        e.stopPropagation();
      }
    }
  },
  created: function() {
    this.isMember = this.ChatViewStoreState.user && this.ChatViewStoreState.user.user_id.indexOf('vis-') != 0;
    this.notification_email = _.get(this.ChatViewStoreState.user, 'values.identify.notification_email.any') || '';
    this.internal_notification_email = this.notification_email;
  }
}

